import React from 'react';
import 'reset-css';
import styled, {css} from 'styled-components';

const StyledButt = styled.button`
({color}) => css;
    label: {label};
    text-align: center;
    font-size: 20px;
    color: ${color};
    text-align: center;
    disabled: {disabled};
    onClick: {onClick};
`;

export const Button = ({ label, onClick = () => {}, disabled, color='aquamarin' }) => {
    return (
        <>
        <StyledButt color={color} onClick={onClick} disabled={disabled}>{label}</StyledButt>
        </>

    );
};
